        private byte[] ConvertImageToByteArray(HttpPostedFileBase file)
        {
            byte[] data;
            using (var ms = new MemoryStream())
            {
                var img = System.Drawing.Image.FromStream(file.InputStream);
                img.Save(ms, img.RawFormat);
                data = ms.ToArray();
                return data;
            }
        }

